import { FC, memo, React } from "react";
import { Modal, ModalHeader, ModalBody, ModalFooter } from "reactstrap";
import { HackAutoFill } from "../HackAutoFill/HackAutoFill";

export const AddNoteModal = (props) => {

  const {
    headerTitle,
    isOpen = true,
    modalBody,
    onClose,
    size = "md",
    id,
    isRtl = false,
    otherProps = {},
    modalFooter,
    bodyClassName = "",
    backdrop = "static",
    keyboard = true,
    hideClose = false
  } = props;

  return (
    <Modal
      isOpen={isOpen}
      toggle={() => {
        onClose();
      }}
      size={size}
      keyboard={keyboard}
      //@ts-ignore
      backdrop={backdrop}
      id={id}
      {...otherProps}
    >
      {headerTitle &&
        (hideClose ? (
          <ModalHeader> {headerTitle} </ModalHeader>
        ) : (
          <ModalHeader
            toggle={() => {
              onClose();
            }}
          >
            {" "}
            {headerTitle}{" "}
          </ModalHeader>
        ))}
      <ModalBody className={` ${bodyClassName} `}>{modalBody}</ModalBody>
      {modalFooter && <ModalFooter>{modalFooter}</ModalFooter>}
      {/* <HackAutoFill /> */}
    </Modal>
  );
}
