import { formatDate } from '../helpers';
import { getNoteById } from '../helpers';
import { Header } from '../Components/Header';
import React, { useState, useEffect } from 'react'
import { Link, useParams } from 'react-router-dom';
import { ReactComponent as ArrowLeft } from '../Assets/arrow-left.svg'

export const NotePage = ({ props }) => {

  const { id } = useParams();
  const [singNote, setSingleNote] = useState(null)

  useEffect(() => {
    getInitialData();
  }, [])

  const getInitialData = async () => {
    const noteData = await getNoteById(id)
    !noteData.hasOwnProperty('error') && setSingleNote(noteData);
  }

  const showData = () => {
    return (
      <div>
        {/* <h1>Note Id : {singNote?.id}</h1> */}
        <textarea defaultValue={singNote?.note_body} onChange={(e) => {setSingleNote({...singNote, note_body: e.target.value});console.log("updated !!!", e.target.va)}}></textarea>
        {/* <h1>Created At : {formatDate(singNote?.created_at)}</h1>
        <h1>Updated At : {formatDate(singNote?.updated_at)}</h1> */}
      </div>
    )
  }

  return (
    <div className='note'>
      <Header />
      <div className='note-header'>
        <h3>
          <Link to="/">
            <ArrowLeft />
          </Link>
        </h3>
      </div>
      {!!singNote ? showData() : <h1>No Note Found</h1>}
    </div>
  )
}
