import React from 'react';
import './Modal.css';

export const Modal = ({ isOpen, onClose, children }) => {
  const closeModal = () => {
    onClose();
  };

  return (
    <>
      <div className={`modal-overlay ${isOpen ? 'show' : ''}`}>
        <div className={`modal ${isOpen ? 'show' : ''}`}>
          {children}
        </div>
      </div>
    </>
  );
};
