import { getAllNotes } from '../helpers';
import { Header } from '../Components/Header';
import { ListItem } from '../Components/ListItem';
import React, { useState, useEffect } from 'react'
import { ReactComponent as AddNote } from '../Assets/plus-button.svg'
// import { AddNoteModal } from '../Components/AddNoteModal';
import { Modal } from '../Components/AddNoteModal';

export const NotesListPage = () => {

    const [notes, setNotes] = useState([]);
    const [modal, setModal] = useState(false);

    useEffect(() => {
        getInitialData();
    }, [])

    const getInitialData = async () => {
        const data = await getAllNotes();
        !data.hasOwnProperty('error') && setNotes(data);
    }

    const modalBody = () => {
        return (
            <div>
                <h2>Enter Note :</h2>
                <textarea defaultValue={''} onChange={() => console.log("textarea")}></textarea>
                <button onChange={() => console.log("Save")}>Save</button>
                <button onChange={() => console.log("Close")}>Close</button>
            </div>
        )
    }

    return (
        <div>
            <Header />
            <div className='notes'>
                <div className='notes-header'>
                    <h2 className='notes-title'>&#9782; Notes</h2>
                    <p className='notes-count'>{notes?.length}</p>
                </div>
                <div className='notes-list'>
                    {notes.map((item, index) => (
                        <ListItem key={`key-${index}`} note={item} />
                    ))}
                    <div style={{
                        "fill": "#f68657",
                        "height": "50px",
                        "width": "50px",
                        "margin": "auto"
                    }}>
                        <AddNote onClick={() => setModal(!modal)} />
                    </div>
                </div>
            </div>
            {/* <Modal isOpen={modal} onClose={() => setModal(!modal)} /> */}
            <Modal
                isOpen={modal}
                modalBody={modalBody}
            />
        </div>
    )
}
